import './card.css';
import { useState } from 'react';
import {X} from 'react-feather';
const Card = ({state,listid,ind,data}) => {
  const [iseditClick,StateEdit] = useState(false);
  const [isdone, Statedone] = useState(false);
  const [editForm,StateForm] = useState(false);

  function edittodo(){
    StateEdit(!iseditClick);
  
  }
    return (
        <div className="card">
              <div className="card-top">
                <div className="card-labels">
                      <div className="card-title" >
                      <div onClick={()=>Statedone(!isdone)} className={'money-bag'} style={
                        isdone?{background : 'linear-gradient(135deg, #BBB5E2 0%, #9C92DF 100%)'}:{background : 'linear-gradient(135deg, gray 0%,#353945 100%)'}
                      }>
                        <img src="icons/mb.png"  />
                      </div>
                     <p> {data.title}</p>
                    </div>
                </div>
                <X alt="delete"/>
                <img onClick={()=>edittodo()} src={iseditClick?("icons/blue-edit.png"):"icons/black-edit.png"} className='edit-btn'></img>
                
              </div>
             
              <div className="card-footer">
               <p>{data.description}</p>
              </div>
        </div>
     );
}
 
export default Card;
