import './todo.css';
import { useState } from 'react';
import RightNavBar from '../rightsidebar/Navbarright';
import Board from '../Todo-board/Boards';
const Home = () => {

    const [fixe,stateFix]=useState(false);
    function fixedstate(){
      if(window.scrollY>=71){
        stateFix(true);
      }else{
        stateFix(false);
      }
    }
    console.log(window.scrollY);
    window.addEventListener("scroll",fixedstate);
    return (
        <div className="container">
          <div className={fixe?'top-navbar top-navbar-fix':'top-navbar'}>

         
            <div className="section-nav" >
             
                      <div className="section-heading">
                              <h2>Section</h2>
                      </div>
                       <div className="wallet-data">
                            <img src="icons/Wallet.png" alt="" />
                            <div className="w-content">0.2$XYZ</div>
                            <div className="tier">Tier 1</div>
                       </div>
            
                   
            </div>
            </div>
            <div className="main" >
            
              <div className="todo-boards">
                  <Board/>
                  <Board/>
                  <Board/>
                  <Board/>
                  
            </div>
            <RightNavBar/>
            </div>
        </div>
      );
}
 
export default Home;
