import './style.css';
import {useState} from 'react';
const Navbar = () => {
    const [fix,statFix]=useState(false);
    const [fixe,stateFix]=useState(false);
    function fixedstate(){
      if(window.scrollY>=71){
        stateFix(true);
      }else{
        stateFix(false);
      }
    }
    console.log(window.scrollY);
    window.addEventListener("scroll",fixedstate);
    const [isExpended,setExpended] = useState(true);
    const menuItems = [
        {
            text:"Home",
            icon: "icons/Category.png"
        },
        {
            text:"Completed",
            icon: "icons/section1.png"
        },
        {
            text:"Incomplete",
            icon: "icons/section2.png"
        },
        {
            text:"History",
            icon: "icons/section31.png"
        }
    ]
    return ( 
        <div className={isExpended? "side-nav-container":"side-nav-container side-nav-container-NX"}>
           <div className="nav-upper">
            <div className="nav-heading">
                {isExpended &&
                <div className="nav-brand">
                    
                    <span><center>N</center></span>
                    <h2>Name</h2>
                </div>}
                <button className={isExpended? "hamburger hamburger-in": "hamburger hamburger-out"}
                 onClick =  {() => setExpended(!isExpended)}
                >
                    
                    <span></span>
                    <span></span>
                    <span></span>
                </button>
            </div>
            <div className="nav-menu">
                    {
                        menuItems.map(
                            (item)=>{
                                return(
                                <a href="#" className={isExpended? "menu-item": "menu-item menu-item-NX"}>
                                     <img src={item.icon} alt="" />
                                     {isExpended &&<p> {item.text}</p>}
                                </a>
                                );
                            }
                        )
                    }
            </div>
           </div>
           {isExpended && <div className="nav-footer">
               <div className="w-profile">
                      
                            <div className="box1">
                                <div className="pic">
                                        <p>N</p>
                                    </div>
                                    <div className="cash-update">
                                        $0.90
                                    </div>
                             </div>
                                
                            
                            <div className="ad">
                            Buy $XYZ
                             </div>
                      
                       
               </div>
               <div className="footer-content">

               </div>
           </div>}
           {/* <div className="nav-footer">
				{isExpended && (
					<div className="nav-details">
						<img
							className="nav-footer-avatar"
							src="icons/section1.svg"
							alt=""
							srcset=""
						/>
						<div className="nav-footer-info">
							<p className="nav-footer-user-name">M Showkat</p>
							<p className="nav-footer-user-position">store admin</p>
						</div>
					</div>
				)}
				<img className="logout-icon" src="icons/Category.png" alt="" srcset="" />
			</div> */}
        </div>
     );
}
 
export default Navbar;
