import './boards.css';
import Card from './Cards';
import AddTodo from './AddTodo';
import { X } from 'react-feather';


const Board = ({state,listid,todos,uniqueid}) => {
    
   const dellist=async(event)=>{
    try{
    //   event.preventDefault();
      const {contract} = state;
      const titleid="#addtitle"+uniqueid;
      const descid = "#AddDescription"+uniqueid;
      const title = document.querySelector(titleid).value;
      const desc = document.querySelector(descid).value;

    //  const mssg=title+" "+desc+" "+titleid;
    //  console.log("mssg:",mssg);
      const transaction = await contract.addTodo(listid,title,desc);
      await transaction.wait();
      alert("transaction is successfully added ");
      window.location.reload();
    }catch(error){
      alert(error);
    }
  }
    return ( 
        <div className='board'>
        <div className="board-top">
        <div className="board_title">
        List : {listid}
        </div>
        <div className='cross'><X /></div>
        

        </div>
        <div className="board-cards custom-scroll-y">
            <AddTodo state={state} listid={listid} uniqueid={uniqueid} />

            
            {todos.length!==0 && todos.map((item,index)=>{
                return <Card state={state} listid={listid} key={index} data={item}/>
            })}
        
            
            
            
        </div>
      </div>
     );
}
 
export default Board;
