import './addlist.css';
import { useState } from 'react';
const AddList = () => {
    
    const [typing,setTyping] = useState(false);
    return ( 
        <div className="addlist">
            <div className="board-top">
            
             <form >
                   <input onChange={()=>{useState(true);}} type="text" style={typing?{color:"white"}:{color:'#6C6C6C'}} className="addlist_XP" id="AddListID" placeholder='ADD TODO LIST' required/>
                   <button className='list-btn'>
                    <img src="icons/white add.png" alt="" />
                   </button>
             </form>
                
                
            
              
            </div>
        </div>
     );
}
 
export default AddList;
