import './card.css';
import { useState } from 'react';
import {X} from 'react-feather';
const Card = ({state,listid,ind,data}) => {
  const [iseditClick,StateEdit] = useState(false);
  const [isDone, Statedone] = useState(data.isdone);
  const [editForm,StateForm] = useState(false);
 
  
  const deltodo=async()=>{
    try{

      const {contract} = state;
      
      const transaction = await contract.deleteTodo(data.id,listid);
      await transaction.wait();
      alert("transaction is successfully added ");
      window.location.reload();
    }catch(error){
      alert(error);
    }
  }

  const done=async()=>{
    try{
    
      const {contract} = state;
      
      const transaction = await contract.Completed(data.id,listid);
      await transaction.wait();
      alert("transaction is successfully added ");
      Statedone(data.isdone);

      window.location.reload();
    }catch(error){
      alert(error);
    }
  }
  function edittodo(){
    StateEdit(!iseditClick);
  
  }
    return (
        <div className="card" key={ind}>
              <div className="card-top">
                <div className="card-labels">
                      <div className="card-title" >
                      <div onClick={()=>{done()}} className={'money-bag'} style={
                        data.isDone?{background : 'linear-gradient(135deg, #BBB5E2 0%, #9C92DF 100%)'}:{background : 'linear-gradient(135deg, gray 0%,#353945 100%)'}
                      }>
                        <img title="click if done" src="icons/mb.png"  />
                      </div>
                     <p> {data.title} </p>
                    </div>
                </div>
                <X alt="delete" onClick={()=>{deltodo()}}/>
                <img  title="edit todo" onClick={()=>edittodo()} src={iseditClick?("icons/blue-edit.png"):"icons/black-edit.png"} className='edit-btn'></img>
                
              </div>
             
              <div className="card-footer">
               <p>{data.description}</p>
              </div>
        </div>
     );
}
 
export default Card;
