import './addtodo.css';
import {useState} from 'react';
const AddTodo = () => {
   const [typingTitle,settypingTitle]=useState(false);
   const [typingDescription,settypingDescription]=useState(false);

    return (
        <div className="AddTodo">
        <form>
        <div className="addtodo-form">
              <div className="input-container">
                <div className="addtodo-top">
                      <div className={'addtodo-money-bag'} style={
                        {background : 'linear-gradient(135deg, #BBB5E2 0%, #9C92DF 100%)'}
                      }>
                        <img src="icons/mb.png"  />
                      </div>
                     <input type="text" placeholder='Add Todo' input="addtitle" onChange={()=>{settypingTitle(true)}} style={typingTitle?{color:"white"}:{color:"#6E6E6E"}} required/>
                 </div>
                <textarea name="desc" id="AddDescription" placeholder='Add Todo Description' onChange={()=>{settypingDescription(true)}} style={ typingDescription ?{color:"white"}:{color:"#808191"}}  required></textarea>
                </div>
                <img src="icons/black-edit.png" className='edit-btn'></img>
                </div>
              </form>
        </div>
     );
}
 
export default AddTodo;
