import './todo.css';
import { useState,useEffect } from 'react';
import RightNavBar from '../rightsidebar/Navbarright';
import Board from '../Todo-board/Boards';
import AddList from '../Todo-board/AddList';
const Home = ({state}) => {
    const [Todos,setTodos] = useState([]);
    const {contract} = state;
    const [ctn,setCtn]=useState(0);

    const TodoMessage=async()=>{
      const acc= await contract.signer.getAddress();
      const todos=await contract.getTodos(acc);
    
       alert(acc);
      setTodos(todos);
      console.log("this is the todos",Todos);
   }   
   
    useEffect(()=>{
       
       contract && TodoMessage();
    },[contract,Todos]);
    
    function fun(){
      setCtn(ctn+1);
      return ctn;
    }
    return (
        <div className="container">
          <div className={'top-navbar'}>

         
            <div className="section-nav" >
             
                      <div className="section-heading">
                              <h2>Section</h2>
                      </div>
                       <div className="wallet-data">
                            <img src="icons/Wallet.png" alt="" />
                            <div className="w-content">0.2$XYZ</div>
                            <div className="tier">Tier 1</div>
                       </div>
            
                   
            </div>
            </div>
            <div className="main" >


            {Todos.map((list)=>{
                setCtn(ctn+1);
                return <Board state={state} listid={list.id} key={list.id} todos={list[1]} uniqueid={ctn}/>
            })}
                  


                  <AddList state={state}/>
          
              <div className="right-drawer">
{/* <RightNavBar/> */}
              </div>
            </div>
        </div>
      );
}
 
export default Home;
