import './todo.css';
import { useState,useEffect } from 'react';
import RightNavBar from '../rightsidebar/Navbarright';
import Board from '../Todo-board/Boards';
import AddList from '../Todo-board/AddList';
const Home = ({state,acc}) => {
    const [Todos,setTodos] = useState([]);
    const {contract} = state;
    const [visible,setVisible]=useState(false);
    const [updatetodo,setUpdate]=useState([]);

    function addForm(itemobj){
      
       setVisible(true);
       setUpdate(itemobj);
    }
    
    useEffect(()=>{
       const TodoMessage=async()=>{
          
        try{
          const todos=await contract.getTodos(acc);
        
          //  alert(acc);
          setTodos(todos);
          console.log("this is the todos",Todos);
        }catch(error){
          console.log(error);
        }
       }   
       
       contract && TodoMessage();
    },[contract]);
    
    
    const editForm=async(event)=>{
      try{
        event.preventDefault();
        const {contract} = state;

        const listid = updatetodo[0];
        const todotitle = document.querySelector("#update-title").value;
        if(todotitle===undefined){
          todotitle=updatetodo[1].title;
        }

        const tododescription = document.querySelector("#update-description").value;
        if(tododescription===undefined){
          console.log("desc")
          tododescription=updatetodo[1].description;
        }

        console.log("filled values",typeOf(todotitle), " ", tododescription);
        // const transaction = await contract.removeList(listid);
        // await transaction.wait();
        // alert("transaction is successfully added ");
        // window.location.reload();
        // setVisible(false);
      }catch(error){
        alert(error);
      }
    }
    return (
        <div className="container">
        
          <div className={'top-navbar'}>

         
            <div className="section-nav" >
             
                      <div className="section-heading">
                              <h2>Section</h2>
                      </div>
                       <div className="wallet-data">
                            <img src="icons/Wallet.png" alt="" />
                            <div className="w-content">0.2$XYZ</div>
                            <div className="tier">Tier 1</div>
                       </div>
            
                   
            </div>
            </div>
            <div className="main" >
           
           <div className="board-container">
           {Todos.map((list)=>{
                  
                  return <Board state={state} addForm={addForm} listid={list.id} key={list.id} todos={list[1]} uniqueid={list.id}/>
              })}

              <AddList state={state}/>
            
           </div>
          {visible && <div className="form-drawer">
                
                 <div className="form-head">
                       <div className="arrow-img">
                          <img src="icons/Path.png" alt="arrow" />
                       </div>
                       <div className="form-title">
                          Edit Todo
                       </div>
                 </div>
                 <div className="update-form">
                         <form onSubmit={(e)=>editForm(e)}>

                           <input type="text" className="" id="update-title" placeholder={updatetodo[1].title}  />
                           <textarea name=""  id="update-description" placeholder={updatetodo[1].description}></textarea>
                           <button>Save</button>
                         </form>
                 </div>
           </div>}
     
  {/* 
              {Todos.map((list)=>{
                  
                  return <Board state={state} addForm={addForm} listid={list.id} key={list.id} todos={list[1]} uniqueid={list.id}/>
              })}
                    


                    <AddList state={state}/>
            
                    {visible &&  <div className="right-drawer">
  <RightNavBar state={state} updatetodo={updatetodo} visible={visible}/>
                </div>} */}
            </div>
            
        </div>
      );
}
 
export default Home;
