import './addtodo.css';
import {useState} from 'react';
const AddTodo = () => {
    return ( 
        <div className="AddTodo">
        <form>
        <div className="addtodo-form">
              <div className="input-container">
                <div className="addtodo-top">
                      <div className={'addtodo-money-bag'} style={
                        {background : 'linear-gradient(135deg, #BBB5E2 0%, #9C92DF 100%)'}
                      }>
                        <img src="icons/mb.png"  />
                      </div>
                     <input type="text" placeholder='Add Todo' input="addtitle" required/>
                 </div>
                <textarea name="desc" id="AddDescription" placeholder='Add Todo Description' required></textarea>
                </div>
                <img src="icons/black-edit.png" className='edit-btn'></img>
                </div>
              </form>
        </div>
     );
}
 
export default AddTodo;
